MimeBodyPart imagePart = new MimeBodyPart();
FileDataSource fds = new FileDataSource("THEIMAGEFILENAME");
imagePart.setDataHandler(new DataHandler(fds));
imagePart.setHeader("Content-ID", "someimage_id"); mp.addBodyPart(htmlPart);
mp.addBodyPart(imagePart);
